import Navbar from "./Navbar";
import { useState, useEffect } from "react";
import { collection, addDoc, doc, setDoc } from "firebase/firestore";
import { db } from "./firebase";

function Blog() {
  const [title, setTitle] = useState("");
  const [postContent, setPostContent] = useState("");
  function submithandle() {
    alert(1);
    setDoc(doc(db, "user", "post"), {
      title: title,
      content: postContent,
    });
  }
  return (
    <div className="max-w-[1400px] h-auto mx-auto">
      <Navbar />

      <div className="h-[100vh]">
        <h1 className="text-white text-2xl">Keto diet for sugar patients</h1>

        <form onSubmit={submithandle} className="flex flex-col gap-4">
          <input
            type="text"
            className="w-[500px] h-[30px] "
            placeholder="Enter Title"
            onChange={(e) => setTitle(e.target.value)}
            required
          />
          <input
            type="text"
            className="w-[500px] h-[30px] "
            placeholder="Enter Title"
            onChange={(e) => setPostContent(e.target.value)}
            required
          />{" "}
          <button className="w-[100px] h-[40px] bg-red-400" type="submit">
            post
          </button>
        </form>
      </div>
    </div>
  );
}

export default Blog;
