import React from "react";
import Navbar from "./Navbar";
import { useState, useEffect } from "react";
import { collection, addDoc, doc, setDoc, getDocs } from "firebase/firestore";
import { db } from "./firebase";
import profile from "./profile.jpg";
import PostModel from "./Model/PostModel";

function Blog() {
  const [posts, setPosts] = useState([]);
  const [modelopen, setModelopen] = useState(false);

  useEffect(() => {
    async function getdata() {
      const querySnapshot = await getDocs(collection(db, "posts"));
      setPosts(
        querySnapshot.docs.map((doc) => ({ ...doc.data(), id: doc.id }))
      );
    }
    getdata();
  }, []);

  console.log(posts.title);

  function Modelfn() {
    setModelopen(true);

    document.body.style.overflow = "hidden";
  }

  return (
    <div className="max-w-[1400px] h-auto mx-auto bg-[#202124] ">
      <Navbar />
      {modelopen && <PostModel closemodal={setModelopen} />}

      <div className="bg-[#333] flex gap-3 items-center px-10 my-10 w-full h-[100px]">
        <img src={profile} alt="" className="w-20" />
        <input type="text" className="w-full h-full" />
      </div>

      {posts.map((post) => {
        return (
          <div className="bg-[#333] mt-5 p-10">
            <h1 className="text-4xl text-white">{post.title}</h1>
            <p className="text-white my-6">{post.content}</p>
          </div>
        );
      })}
    </div>
  );
}

export default Blog;
